void input(int linea){
    for(int i=0;i<100;i++){
        if(flechas[i][0]>=90 && flechas[i][0]<=96){
            if(flechas[i][1]==linea){
                color(BLANCO);
                circulo_lleno((flechas[i][0]+1)*5, 75*(linea+1),27);
                puntos=puntos+15;
            }
        }
    }



}

void leer(int tamano, string codigo, int x, int y){
    int contador=0;
    char arr[tamano*tamano];
    strcpy(arr,codigo.c_str());
    for(int i=0;i<tamano;i++){
        for(int j=0;j<tamano;j++){
             if(int(arr[contador])-48==1){
                color(BLANCO);
                cuadro((i*5)+x,(j*5)+y);
             }
             contador++;
        }
    }


}

