void displayPuntos(){
    int dig=0;
    int cent, dec, uni;
    if(puntos2!=puntos){
        color(NEGRO);
        rectangulo_lleno(150,330,500,480);
        if(puntos/100!=0){
            cent=puntos/100;
            displayNum(cent,dig);
            dig++;
        }
        dec=puntos%100;
        dec=dec/10;
        displayNum(dec,dig);
        dig++;
        uni=puntos%100;
        uni=uni%10;
        displayNum(uni, dig);
        dig=0;
        puntos2=puntos;
    }



}

